mapper.select:
SELECT * FROM (SELECT a.food_seq, a.food_name, a.food_expi_date, a.food_img, a.category_seq, b.category_name FROM food a LEFT JOIN category b ON a.category_seq = b.category_seq WHERE a.is_delete = 'N') c WHERE
c.category_name REGEXP #{searchText} OR c.food_name REGEXP #{searchText}
ORDER BY c.category_seq, c.food_name;

SELECT a.food_seq, a.food_name, a.food_expi_date, a.food_img, a.category_seq, b.category_name, b.ord FROM food a LEFT JOIN category b ON a.category_seq = b.category_seq WHERE
a.category_seq = #{categorySeq} AND
a.is_delete = 'N' ORDER BY ord, food_name;
